package com.problemsolving;

public class UpperCasePrinting {

	static void upperCase() {
		//using ASCII values  A-65  Z-90

		for (int i = 65; i <= 90; i++) {
			System.out.print((char) i + " ");

		}

	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		upperCase();

	}

}
