package demo;

public class PrimitivesDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//integer,byte,short,float,double,char,long,boolean,string
		
		byte b1=21;            //range 127
		
		short s1=30000;        //-32,768 to 32,767
		
		int i1=100;             //-2,147,483,648 to 2,147,483,647
		
		long l1=1000000000;      //-9,223,372,036,854,775,808 to 9,223,372,036,854,775,807
		
		float f1=30.00f;             //Sufficient for storing 6 to 7 decimal digits
		
		double d1=30.123456d;         //sufficient for storing 15 to 16 decimal digits
		
		char c1='V';                 //Stores a single character/letter or ASCII values
		
		boolean B1=false;
		
		String s="vinay";
		
		
		System.out.println(b1);
		System.out.println(s1);
		System.out.println(i1);
		System.out.println(l1);
		System.out.println(f1);
		System.out.println(d1);
		System.out.println(c1);
		System.out.println(B1);
		System.out.println(s);





		

	}

}
